data class stopsClass(
    val id: Int,
    val nombre: String,
    val posx: Double,
    val posy: Double,
    val enlaces: List<Int>
)
data class busesClass(
    val id: String,
    val nom_comer: String,
    val color_linea: String,
    val orig_linea: String,
    val dest_linea: String,
    val dest_ida: String,
    val dest_vuelta: String
)

val gson = Gson()
val stopJSON = gson.fromJson(stops, Array<stopsClass>::class.java).toList()
val busJSON = gson.fromJson(buses, Array<busesClass>::class.java).toList()

fun stopName(number: String): String {
    val desiredId = number.toInt() // Convert the desiredId to Int

    var loc = stopJSON.find { it.id == desiredId }?.nombre.toString()

    return if (loc != "null") {
        loc
    } else {
        loc = "Parada inválida"
        loc
    }
}

fun getNomComerById(id: String): String? {
    var loc = busJSON.find { it.id == id }?.nom_comer

    return if (loc != "null") {
        loc
    } else {
        loc = "???"
        loc
    }
}

fun getColorByNom(nom: String): Int {
    var col: String
    if (nom == "0") {
        col = "000000"
    } else {
        col = busJSON.find { it.nom_comer == nom }?.color_linea.toString()
        if (col == "null") {
            col = "000000"
        }
    }
    return Color.parseColor("#$col")
}

fun getParadasArray(): Array<String> {
    val locationType = object : TypeToken<List<stopsClass>>() {}.type
    val locationList: List<stopsClass> = gson.fromJson(stops, locationType)

    val stopList = locationList.map { "${it.id} - ${it.nombre}" }

    return stopList.toTypedArray()
}

fun getFirstNumbers(input: String): Int? {
    val regex = """^(\d+)""".toRegex()
    val matchResult = regex.find(input)
    return matchResult?.groupValues?.get(1)?.toIntOrNull()
}

fun getClosestLocation(userLatLong: Pair<Double, Double>?): stopsClass? {
    val userLocation = Location("User")
    println(userLatLong)
    if (userLatLong != null) {
        println(userLatLong)
        userLocation.latitude = userLatLong.second
        userLocation.longitude = userLatLong.first
    }

    var closestLocation: stopsClass? = null
    var closestDistance = Float.MAX_VALUE

    for (location in stopJSON) {
        val destinationLocation = Location("Destination")
        destinationLocation.latitude = location.posy
        destinationLocation.longitude = location.posx

        val distance = userLocation.distanceTo(destinationLocation)
        if (distance < closestDistance) {
            closestDistance = distance
            closestLocation = location
        }
    }

    return closestLocation
}